function submitTranscription(i){$.post("/admin/review/accept",{id:i,transcription:$("#text").val(),languages:$("#language-editor").val()}).done(function(){window.location.href="/admin/review"})}function rejectTranscription(i){$.post("/admin/review/reject",{id:i,transcription:$("#text").val(),languages:$("#language-editor").val()}).done(function(){window.location.href="/admin/review"})}function saveTranscription(i){$.post("/admin/review/save",{id:i,transcription:$("#text").val(),languages:$("#language-editor").val()}).done(function(){$("#body").prepend("<div class = 'notification is-green'><button class = 'delete' onclick = '$(this).parent().hide()'></button><p>Successfully saved edit!</p></div>")})}$(document).ready(function(){$("#img").css("max-width",Math.min($("#img").width(),$("#editor").width()/2)),$("#img").resizable({handleSelector:"#splitter",resizeHeight:!1})});